import React, {useContext} from 'react';
import {RecetasContext} from '../context/RecetasContext';
import Receta from './Receta';
const ListaRecetas = () => {

    // extraer las recetas
    const {recetas} = useContext(RecetasContext);
    return (  
        <div className="row mt-5">
            {recetas.map(receta => (
                <Receta
                    key={receta.idDring}
                    receta={receta}
                />
            ))}
        </div>
    );
}
 
export default ListaRecetas;
